mapper.insert:
select sys_guid() from dual
INSERT INTO TB_WX_CHAT_SESSION wxchatsession ( wxchatsession.SESSION_ID, wxchatsession.ACCOUNTID, wxchatsession.OPENID, wxchatsession.CREATETIME, wxchatsession.CREATELONG, wxchatsession.UPDATETIME, wxchatsession.C_CONTENT, wxchatsession.ENDTIME, wxchatsession.FANID ) VALUES( #{session_id:VARCHAR}, #{accountid:VARCHAR}, #{openid:VARCHAR}, sysdate, #{createLong:NUMERIC}, sysdate, #{c_content:VARCHAR}, #{endtime:TIMESTAMP}, #{fanId:VARCHAR}
)

mapper.delete:
DELETE FROM TB_WX_CHAT_SESSION
wxchatsession WHERE
wxchatsession.SESSION_ID=#{session_id}
