mapper.select:
SELECT * FROM TB_ORG_INFO t
where t.USEFLAG=1

	and t.COMPANYID=#{companyId}

SELECT * FROM TB_ORG_INFO orginfo WHERE 1=1 and orginfo.ORGTYPE=1
and orginfo.ORGID=#{companyId}
order by orginfo.UPDATETIME desc

select t.*, p2.orgname parentName, (case when cnum is null then 'open' else 'closed' end) state from tb_org_info t left join tb_org_info p2 on t.parentid = p2.orgid left join (select count(1) cnum, p.parentid pid from tb_org_info p group by p.parentid) p on t.orgid = p.pid where 1=1
AND t.parentid IN
#{ids}
and t.COMPANYID=#{companyId}
