mapper.select:
SELECT * FROM TB_MALL_TRACE_LOG t WHERE 1=1 and t.companyId=#{companyId}
and ( 2=1 or t.TRACEID LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.BARCODE LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.GOODNO LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.BARNO LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.GOODNAME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.TYPENAME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.BRANDNAME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.ORGNAME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') )
order by t.CREATETIME DESC
