mapper.select:
SELECT concat_ws('',t.tranYear,'年',t.tranMonth,'月') AS itemName, sum(CASE WHEN t.tranType = 0 THEN t.tranAmount ELSE 0.00 END) outAmount FROM TB_ASSET_LOG t WHERE 1=1 and t.USERID=#{userid}
and ( 2=1 or t.ITEMNAME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.FROMER LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.TOER LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.TRANYEAR LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.TRANMONTH LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.IDNAME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.remarks LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') )
GROUP BY t.userid, t.tranYear,t.tranMonth order by t.tranYear DESC,t.tranMonth DESC LIMIT 12

SELECT * FROM TB_ASSET_LOG t WHERE 1=1 and t.USERID=#{userid}
and ( 2=1 or t.ITEMNAME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.FROMER LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.TOER LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.TRANYEAR LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.TRANMONTH LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.IDNAME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or t.remarks LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') )
order by t.TRANTIME DESC,t.UPDATETIME DESC
