mapper.select:
SELECT DISTINCT VSRM.MENUID, VSRM.MENUITEM, VSRM.PARENTID, VSRM.URL, vsrm.nsort FROM VIEW_SYS_ROLE_MENU vsrm WHERE 1=1
AND VSRM.ROLEID =#{roleId}
and vsrm.PARENTID != '0' order by vsrm.nsort

SELECT distinct VSRF.OPERID, VSRF.OPERNAME, VSRF.PICICON, vsrf.sort FROM VIEW_SYS_ROLE_FUNC vsrf where 1=1
AND vsrf.ROLEID =#{roleId}
AND VSRF.MENUID = #{menuid} order by vsrf.sort
