mapper.select:
SELECT sysrole.*,o.orgname companyName FROM TB_SYS_ROLE sysrole left join tb_org_info o on sysrole.companyId=o.orgid WHERE 1=1
and sysrole.COMPANYID = #{companyId}
and sysrole.lvl >= #{lvl} and sysrole.SHOWFLAG=1 ORDER BY sysrole.COMPANYID,sysrole.lvl

SELECT sysrole.* FROM TB_SYS_ROLE sysrole WHERE 1=1
and sysrole.COMPANYID = #{companyId}
and sysrole.ROLEID != #{loginUserRoleId}
and ( sysrole.ROLEID LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or sysrole.ORGID LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%') or sysrole.UPDATETIME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%')
or sysrole.CREATOR LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%')
or sysrole.ROLENAME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%')
or sysrole.CREATETIME LIKE CONCAT(CONCAT('%',#{keyword:VARCHAR}),'%')
)
and sysrole.lvl = #{s_lvl}
and sysrole.lvl >= #{lvl}
ORDER BY sysrole.COMPANYID,sysrole.UPDATETIME desc

SELECT distinct tso.OPERID, tso.OPERNAME, tso.sort, tsf.MENUID, tsf.FUNCID, t2.state FROM TB_SYS_FUNC tsf JOIN TB_SYS_OPERATE tso on tsf.operid=tso.operid left join tb_sys_role_func tsrfn on tsf.funcid=tsrfn.funcid left join (select DISTINCT tsrf.funcid, 'checked' state from tb_sys_role_func tsrf where tsrf.roleid=#{roleId:VARCHAR} ) t2 on t2.funcid = tsf.funcid where 1=1
AND tsrfn.ROLEID =#{loginUserRoleId}
order by tso.sort

select * from
tb_sys_role t where t.roleType=#{roleType} and
t.companyid=#{companyId}
